package com.idea.memories.Classes;public class Memory {    private String title;    private String desc;    private String date;    private String colorHex;    private MediaFile mainPhoto;    private MediaFile[] mediaFiles;    private boolean favored;    private boolean deleted;    private int DBPosition;    private int position;    public Memory(int DBPosition, int position , String title, String desc, String date, MediaFile mainPhoto, MediaFile[] mediaFiles, String colorHex , boolean favored, boolean deleted) {        this.DBPosition = DBPosition;        this.position = position;        this.title = title;        this.desc = desc;        this.date = date;        this.mainPhoto = mainPhoto;        this.mediaFiles = mediaFiles;        this.colorHex = colorHex;        this.favored = favored;        this.deleted = deleted;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getDesc() {        return desc;    }    public void setDesc(String desc) {        this.desc = desc;    }    public String getDate() {        return date;    }    public void setDate(String date) {        this.date = date;    }    public MediaFile getMainPhoto() {        return mainPhoto;    }    public void setMainPhoto(MediaFile mainPhoto) {        this.mainPhoto = mainPhoto;    }    public MediaFile[] getMediaFiles() {        return mediaFiles;    }    public void setMediaFiles(MediaFile[] mediaFiles) {        this.mediaFiles = mediaFiles;    }    public String getColorHex() {        return colorHex;    }    public void setColorHex(String colorHex) {        this.colorHex = colorHex;    }    public boolean isFavored() {        return favored;    }    public void setFavored(boolean favored) {        this.favored = favored;    }    public boolean isDeleted() {        return deleted;    }    public void setDeleted(boolean deleted) {        this.deleted = deleted;    }    public int getPosition() {        return position;    }    public void setPosition(int position) {        this.position = position;    }    public int getDBPosition() {        return DBPosition;    }    public void setDBPosition(int DBPosition) {        this.DBPosition = DBPosition;    }}